css:
.container-table100 {
  width: 100%;
  min-height: 100vh;


  display: -webkit-box;
  display: -webkit-flex;
  display: -moz-box;
  display: -ms-flexbox;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  padding: 33px 30px;
}

.wrap-table100 {
  width: 1170px;
}

table {
  border-spacing: 1;

  background: white;
  border-radius: 10px;
  overflow: hidden;
  width: 100%;
  margin: 0 auto;
  position: relative;
bottom: 25px;
}

table td, table th {
  padding-left: 15px;
}
table thead tr {
  height: 60px;
  background: #36304a;
}
table tbody tr {
  height: 50px;
}


.table100-head th{
  font-family: URW Chancery L, cursive	;
  font-size: 20px;
  color:#AB0A42;
  line-height: 0.9;
  font-weight: unset;
}



tbody tr {
  font-family:URW Chancery L, cursive	;
  font-size: 15px;
  color: #808080;
  line-height: 1.2;
  font-weight: unset;
}




  table tbody tr td:before {
    font-family: Courier;
    font-size: 14px;
    color: #999999;
    line-height: 1.0;
    font-weight: unset;
    position: absolute;
    width: 40%;
    left: 30px;
    top: 0;
  }
